def ArrayToPygameImage(asciiMap, color=(255, 255, 255, 255)):
    """
    Converts 2D arrays of ints to a pygame image.
    """
    image = pygame.Surface((len(asciiMap[0]), len(asciiMap)), pygame.SRCALPHA, 32)
    image.convert_alpha()
    image.fill((0, 0, 0))

    for y in range(len(asciiMap)):
        for x in range(len(asciiMap[y])):
            if asciiMap[y][x] == 1:
                image.set_at((x, y), color)
            else:
                image.set_at((x, y), (0, 0, 0, 0))

    image.set_colorkey((0, 0, 0), pygame.RLEACCEL)

    return image
